
public class Arr10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] sArr1 = {"사과","배","포도"};
		
		for(int i=0; i< sArr1.length; i++) {
			System.out.print(sArr1[i]+" ");
		}		
		System.out.println(" ");
		for(String s: sArr1) {
			System.out.print(s+" ");
		}
	
	}

}
